import React from 'react';
import '../assets/Form.scss';
import { connect } from 'react-redux';
import { reduxForm, Field } from 'redux-form';
import { makeOrder } from '../../actions';
import { Input, Textarea, Select } from './Input';

class OrderForm extends React.Component {
	onSubmit = (formValues) => {
		makeOrder(formValues);
	}
	
	render() {
		return (
			<form onSubmit={() => this.props.handleSubmit(this.onSubmit)} className="form">
				<Field name="name" label="Имя" component={Input} />
				<Field name="phone" label="Телефон" component={Input} />
				<Field name="name" label="Товар" component={Select} />
				<Field name="name" label="Комментарии" component={Textarea} />
				<input type="submit" value="Заказать" className="form-button" />
			</form>
		)
	}
}

const orderForm = reduxForm({form: 'makeOrder'})(OrderForm)
export default connect(null, {makeOrder})(orderForm);
