import React from 'react';
import '../assets/Form.scss';
import { reduxForm, Field } from 'redux-form';
import { makeOrder } from '../../actions';
import { Input, Textarea, Select } from './Input';

class OrderForm extends React.Component {
	onSubmit = (formValues) => {
		makeOrder(formValues);
	}
	render() {
		return (
			<form action="/api/orders" method="post" className="form">
				<Input type="text" label="Имя" name="name" />
				<Input type="text" label="Мобильный телефон" name="phone" />
				<Select type="text" label="Товар" name="item" />
				<Textarea label="Комментарии" name="comment" />
				<input type="submit" value="Заказать" className="form-button" />
			</form>
		)
	}
}
const orderForm = connect(null, { makeOrder })(OrderForm);
export default reduxForm({ form: 'makeOrder' })(OrderForm);
