import React from 'react';
import './Delivery.scss';
import { deliverySteps, deliveryHeaders } from './deliverySteps';

const Delivery = ({ height, openSteps }) => {
    const showSteps = () => {
        return deliverySteps.map((step, idx) => {
            return (
                <div key={idx} className="delivery_step">
                    <div className="delivery_step-img">
                        <img src={step.icon} />
                    </div>
                    <div className="delivery_step-info">
                        <div className="delivery_step-header">
                            {step.name.ru}
                        </div>
                        <div className="delivery_step-text">
                            {step.text.ru}
                        </div>
                    </div>
                </div>
            )
        })
    }

    return (
        <div className="delivery_wrapper">
            <div onClick={() => openSteps()} className="delivery-header">
                <span>Как мы работаем</span><i style={height !== 0 ? {transform: 'rotate(180deg)'} : ''} className="fas fa-chevron-up"></i>
            </div>
            <div style={{maxHeight: height, transition: 'max-height .3s'}} className="delivery_main-box">
                <div className="delivery_steps">
                    {showSteps()}
                </div>
            </div>
        </div>
    )
}

export default Delivery;
