import React from 'react';
import '../assets/Form.scss';
import { Input, Textarea, Select } from './Input';

const OrderForm = () => {
    return (
        <form onSubmit={(e) => e.preventDefault()} className="form">
            <Input type="text" label="Имя" name="name" />
            <Input type="text" label="Мобильный телефон" name="phone" />
            <Select type="text" label="Товар" name="item" />
            <Textarea label="Комментарии" name="comment" />
            <input type="submit" value="Заказать" className="form-button" />
        </form>
    )
}

export default OrderForm;
